import React, { Component } from 'react';

class Js extends Component {
  constructor(props) {
    super(props);
    this.state = {
      el: null,
      chars: '!<>-_\\/[]{}—=+*^?#________',
      queue: [],
      frame: 0,
    };
    this.update = this.update.bind(this);
  }

  componentDidMount() {
    const el = document.querySelector('.text');
    this.setState({ el }, () => {
      this.initializeTextScramble();
    });
  }

  initializeTextScramble() {
    this.fx = new TextScramble(this.state.el);
    this.fx.setText(phrases[0]).then(() => {
      setTimeout(this.next, 800);
    });
  }

  next = () => {
    const { counter } = this.state;
    this.fx.setText(phrases[counter]).then(() => {
      setTimeout(this.next, 800);
    });
    this.setState({ counter: (counter + 1) % phrases.length });
  };

  setText(newText) {
    const oldText = this.state.el.innerText;
    const length = Math.max(oldText.length, newText.length);
    const promise = new Promise((resolve) => (this.resolve = resolve));
    const queue = [];
    for (let i = 0; i < length; i++) {
      const from = oldText[i] || '';
      const to = newText[i] || '';
      const start = Math.floor(Math.random() * 40);
      const end = start + Math.floor(Math.random() * 40);
      queue.push({ from, to, start, end });
    }
    cancelAnimationFrame(this.state.frameRequest);
    this.setState({ frame: 0, queue }, () => {
      this.update();
    });
    return promise;
  }

  update() {
    let output = '';
    let complete = 0;
    for (let i = 0, n = this.state.queue.length; i < n; i++) {
      let { from, to, start, end, char } = this.state.queue[i];
      if (this.state.frame >= end) {
        complete++;
        output += to;
      } else if (this.state.frame >= start) {
        if (!char || Math.random() < 0.28) {
          char = this.randomChar();
          this.state.queue[i].char = char;
        }
        output += `<span class="dud">${char}</span>`;
      } else {
        output += from;
      }
    }
    this.state.el.innerHTML = output;
    if (complete === this.state.queue.length) {
      this.resolve();
    } else {
      this.setState(
        { frameRequest: requestAnimationFrame(this.update), frame: this.state.frame + 1 },
        () => {
          this.update();
        }
      );
    }
  }

  randomChar() {
    return this.state.chars[Math.floor(Math.random() * this.state.chars.length)];
  }

  render() {
    return (
      <div className="text">
        {/* The text will be rendered here */}
      </div>
    );
  }
}

const phrases = ['vem fazer', 'parte', 'do futuro', 'vem de', '€thopay'];

export default Js;
