import React from 'react';

function BotaoAbraSuaConta() {
  const botaoStyle = {
    backgroundColor: '#007bff', // Cor de fundo normal
    color: '#fff', // Cor do texto
    padding: '10px 20px', // Espaçamento interno
    border: 'none', // Sem borda
    cursor: 'pointer', // Cursor de mão ao passar o mouse
    transition: 'background-color 0.3s', // Transição de cor de fundo
  };

  const botaoHoverStyle = {
    backgroundColor: '#0056b3', // Cor de fundo no hover
  };

  return (
    <button
      style={{ ...botaoStyle, ...botaoHoverStyle }}
    >
      Abra sua conta
    </button>
  );
}

export default BotaoAbraSuaConta;
