import React, { useState, useEffect, useRef } from 'react';

const Titulo3 = () => {
  const [text, setText] = useState('');
  const fullText = 'Planos de Vida para Todos, Inclusive Seus Pets';
  const typingSpeed = 70; // Velocidade de digitação (em milissegundos)
  const [shouldType, setShouldType] = useState(false);
  const elementRef = useRef(null);

  const handleScroll = () => {
    if (elementRef.current) {
      const elementPosition = elementRef.current.getBoundingClientRect();
      const windowHeight = window.innerHeight;
      if (elementPosition.top < windowHeight) {
        setShouldType(true);
      }
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Verifica imediatamente ao montar o componente
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    if (shouldType) {
      let currentIndex = 0;
      const typingInterval = setInterval(() => {
        if (currentIndex < fullText.length) {
          setText(fullText.substring(0, currentIndex + 1));
          currentIndex++;
        } else {
          clearInterval(typingInterval);
        }
      }, typingSpeed);

      return () => {
        clearInterval(typingInterval);
      };
    }
  }, [shouldType]);

  return (
    <div ref={elementRef} id="typing-element">
      <h1
        style={{
          position: 'absolute',
          fontFamily: 'monospace',
          color: '#fff',
          fontSize: '45px',
          marginTop: '80px', // Ajuste conforme necessário
          marginLeft: '600px', // Ajuste conforme necessário
        }}
      >
        {text}
      </h1>
    </div>
  );
};

export default Titulo3;
