import React, { useState, useEffect } from 'react';
import Waypoint from 'react-waypoint';

const TypingEffect = () => {
  const [text, setText] = useState('');
  const fullText = 'Texto que parece estar sendo digitado...';
  const typingSpeed = 50; // Velocidade de digitação (em milissegundos)
  const [startTyping, setStartTyping] = useState(false);

  useEffect(() => {
    if (startTyping) {
      const typingInterval = setInterval(() => {
        const currentIndex = text.length;
        if (currentIndex < fullText.length) {
          setText(fullText.substring(0, currentIndex + 1));
        } else {
          clearInterval(typingInterval);
        }
      }, typingSpeed);

      return () => {
        clearInterval(typingInterval);
      };
    }
  }, [text, startTyping]);

  return (
    <div>
      <Waypoint
        onEnter={() => setStartTyping(true)}
        topOffset="50%" // Ajuste conforme necessário para determinar quando o elemento está no viewport
      />
      <h1>{text}</h1>
    </div>
  );
};

export default TypingEffect;
