import React, { useState, useEffect } from 'react';
import './Titulo.module.css';

function ColorChangingText() {
  const [gradientColor, setGradientColor] = useState('#FF0000'); // Cor inicial do gradiente

  useEffect(() => {
    const interval = setInterval(() => {
      setGradientColor((prevColor) => (prevColor === '#FF0000' ? '#00FF00' : '#FF0000'));
    }, 5000); // Mude o gradiente a cada 5 segundos

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="color-changing-text">
      <h1 className="text-mask" style={{ background: `linear-gradient(to right, transparent 50%, ${gradientColor} 50%)` }}>
        TÃ­tulo
      </h1>
    </div>
  );
}

export default ColorChangingText;
