import React, { useEffect, useRef } from 'react';
import Typed from 'typed.js';

const TypingEffect = () => {
  const textRef = useRef(null);

  useEffect(() => {
    const options = {
      strings: ['Texto que parece estar sendo digitado...'], // Array de strings a serem digitadas
      typeSpeed: 50, // Velocidade de digitação (em milissegundos)
      startDelay: 0, // Atraso antes de iniciar a digitação (em milissegundos)
      showCursor: true, // Mostrar cursor piscando
    };

    const typed = new Typed(textRef.current, options);

    return () => {
      typed.destroy(); // Certifique-se de destruir a instância do Typed.js ao desmontar o componente
    };
  }, []);

  return <h1 ref={textRef}></h1>;
};

export default TypingEffect;
