import React, { useState, useEffect } from 'react';

function TypingHeader() {
  const [text, setText] = useState('');
  const fullText = 'Vem fazer parte do futuro';
  const typingSpeed = 50; // Velocidade de digitação (em milissegundos)

  useEffect(() => {
    let currentIndex = 0;
    const intervalId = setInterval(() => {
      if (currentIndex < fullText.length) {
        setText((prevText) => prevText + fullText[currentIndex]);
        currentIndex++;
      } else {
        clearInterval(intervalId);
      }
    }, typingSpeed);

    return () => {
      clearInterval(intervalId); // Limpa o intervalo quando o componente é desmontado
    };
  }, []);

  return (
    <h1>
      {text}
      <span className="cursor">|</span> {/* Adicione um cursor piscando */}
    </h1>
  );
}

export default TypingHeader;
