import React, { useState, useEffect } from 'react';
import { Link, Element } from 'react-scroll';

const TypingEffect = () => {
  const [text, setText] = useState('');
  const fullText = 'Texto que parece estar sendo digitado...';
  const typingSpeed = 50; // Velocidade de digitação (em milissegundos)
  const elementId = 'typingEffectElement';

  useEffect(() => {
    const typingInterval = setInterval(() => {
      const currentIndex = text.length;
      if (currentIndex < fullText.length) {
        setText(fullText.substring(0, currentIndex + 1));
      } else {
        clearInterval(typingInterval);
      }
    }, typingSpeed);

    return () => {
      clearInterval(typingInterval);
    };
  }, [text]);

  return (
    <div>
      <Element name={elementId}>
        <h1>{text}</h1>
      </Element>
      <Link
        activeClass="active"
        to={elementId}
        spy={true}
        smooth={true}
        offset={50} // Ajuste conforme necessário
        duration={500}
      >
        Role para ver o efeito de digitação
      </Link>
    </div>
  );
};

export default TypingEffect;
