import React, { useState } from 'react';

function Botao() {
  const [isHovered, setIsHovered] = useState(false);
  const [isClicked, setIsClicked] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  const handleClick = () => {
    setIsClicked(!isClicked);
  };

  const buttonText = isClicked ? 'Abra sua conta' : '';

  const buttonStyle = {
    backgroundColor: isHovered ? '#007bff' : '#333',
    color: '#fff',
    padding: '10px 20px',
    border: 'none',
    cursor: 'pointer',
  };

  return (
    <button
      style={buttonStyle}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onClick={handleClick}
    >
      {buttonText}
    </button>
  );
}

export default Botao;
