import React, { useState, useEffect } from 'react';


function Titulo() {
  const [color, setColor] = useState('#FF0000'); // Cor inicial do texto
  const [gradientColor, setGradientColor] = useState('#00FF00'); // Cor do gradiente

  useEffect(() => {
    const interval = setInterval(() => {
      setColor((prevColor) => (prevColor === '#FF0000' ? '#00FF00' : '#FF0000'));
      setGradientColor((prevGradientColor) => (prevGradientColor === '#00FF00' ? '#0000FF' : '#00FF00'));
    }, 5000); // Mude a cor a cada 5 segundos

    return () => clearInterval(interval);
  }, []);

  return (
    <h1 style={{ backgroundImage: `linear-gradient(to right, ${color}, ${gradientColor}, ${color})` }} className="color-changing-heading">
      TÃ­tulo
    </h1>
  );
}

export default Titulo;
